'use strict';
// Avoid "Possible EventEmitter memory leak detected" warnings
// because it breaks pnpm's CLI output
process.setMaxListeners(0);
const argv = process.argv.slice(2);
(async () => {
    switch (argv[0]) {
        case '-v':
        case '--version':
            const { version } = (await Promise.resolve().then(() => require('@pnpm/cli-meta'))).default;
            console.log(version);
            break;
        case 'install-completion': {
            const { install: installCompletion } = await Promise.resolve().then(() => require('@pnpm/tabtab'));
            await installCompletion({ name: 'pnpm', completer: 'pnpm' });
            return;
        }
        case 'uninstall-completion': {
            const { uninstall: uninstallCompletion } = await Promise.resolve().then(() => require('@pnpm/tabtab'));
            await uninstallCompletion({ name: 'pnpm' });
            return;
        }
        // commands that are passed through to npm:
        case 'access':
        case 'adduser':
        case 'bin':
        case 'bugs':
        case 'c':
        case 'config':
        case 'deprecate':
        case 'dist-tag':
        case 'docs':
        case 'edit':
        case 'get':
        case 'info':
        case 'init':
        case 'login':
        case 'logout':
        case 'owner':
        case 'ping':
        case 'prefix':
        case 'profile':
        case 'repo':
        case 's':
        case 'se':
        case 'search':
        case 'set':
        case 'star':
        case 'stars':
        case 'team':
        case 'token':
        case 'unpublish':
        case 'unstar':
        case 'v':
        case 'version':
        case 'view':
        case 'whoami':
        case 'xmas':
            await passThruToNpm();
            break;
        default:
            await runPnpm();
            break;
    }
})();
async function runPnpm() {
    const errorHandler = (await Promise.resolve().then(() => require('../err'))).default;
    try {
        const main = (await Promise.resolve().then(() => require('../main'))).default;
        await main(argv);
    }
    catch (err) {
        errorHandler(err);
    }
}
async function passThruToNpm() {
    const runNpm = (await Promise.resolve().then(() => require('../runNpm'))).default;
    const { status } = await runNpm(argv);
    process.exit(status);
}
